import React, { useEffect, useState } from 'react';

const HeroSection = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  // Track mouse position for parallax effect
  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({
        x: e.clientX / window.innerWidth - 0.5,
        y: e.clientY / window.innerHeight - 0.5
      });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  return (
    <section id="hero" className="section bg-hero min-h-screen flex items-center relative overflow-hidden">
      <div className="grid-lines absolute inset-0 opacity-20"></div>

      {/* Animated background particles - Removidas partículas cinzas */}
      <div className="absolute inset-0 z-0">
        {[...Array(15)].map((_, i) => (
          <div 
            key={i}
            className="absolute rounded-full"
            style={{
              width: `${Math.random() * 6 + 2}px`,
              height: `${Math.random() * 6 + 2}px`,
              top: `${Math.random() * 100}%`,
              left: `${Math.random() * 100}%`,
              animation: `floatParticle ${Math.random() * 10 + 15}s linear infinite`,
              animationDelay: `${Math.random() * 5}s`,
              opacity: Math.random() * 0.5 + 0.2,
              background: `rgba(${30 + Math.random() * 50}, ${91 + Math.random() * 50}, ${250 + Math.random() * 5}, ${Math.random() * 0.3 + 0.1})`,
              boxShadow: `0 0 ${Math.random() * 5 + 2}px rgba(30, 91, 250, ${Math.random() * 0.3 + 0.1})`,
              // Movimento mais lento com o mouse
              transform: `translate(${mousePosition.x * -0.3}px, ${mousePosition.y * -0.3}px)`,
              transition: 'transform 0.8s ease-out'
            }}
          ></div>
        ))}
        {[...Array(10)].map((_, i) => (
          <div 
            key={i + 15}
            className="absolute rounded-full"
            style={{
              width: `${Math.random() * 6 + 2}px`,
              height: `${Math.random() * 6 + 2}px`,
              top: `${Math.random() * 100}%`,
              left: `${Math.random() * 100}%`,
              animation: `floatParticle ${Math.random() * 10 + 15}s linear infinite`,
              animationDelay: `${Math.random() * 5}s`,
              opacity: Math.random() * 0.5 + 0.2,
              background: `rgba(${176 + Math.random() * 30}, ${30 + Math.random() * 30}, ${255}, ${Math.random() * 0.3 + 0.1})`,
              boxShadow: `0 0 ${Math.random() * 5 + 2}px rgba(176, 30, 255, ${Math.random() * 0.3 + 0.1})`,
              // Movimento mais lento com o mouse
              transform: `translate(${mousePosition.x * -0.5}px, ${mousePosition.y * -0.5}px)`,
              transition: 'transform 0.8s ease-out'
            }}
          ></div>
        ))}
      </div>

      <div className="container-custom relative z-10">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
          <div 
            className="space-y-8"
            style={{
              // Movimento mais lento com o mouse
              transform: `translate(${mousePosition.x * -2}px, ${mousePosition.y * -8}px)`,
              transition: 'transform 0.6s ease-out'
            }}
          >
            <h1 className="text-4xl md:text-5xl lg:text-6xl font-light leading-tight">
              Transforme seu Negócio com <span className="gradient-text font-normal">IA Personalizada</span>
            </h1>
            <p className="text-lg text-white/80 max-w-xl">
              Soluções de inteligência artificial seguras e acessíveis para pequenas e médias empresas. 
              Automatize processos, impulsione resultados e destaque-se no mercado.
            </p>
            <div className="flex flex-col sm:flex-row gap-4">
              <a href="#solutions" className="btn-primary animate-pulse-slow">
                Conheça Nossas Soluções
              </a>
              <a href="#contact" className="btn-secondary hover:scale-105 transition-transform">
                Agende uma Demonstração
              </a>
            </div>
          </div>

          <div 
            className="relative"
            style={{
              // Movimento mais lento com o mouse
              transform: `translate(${mousePosition.x * -5}px, ${mousePosition.y * -20}px)`,
              transition: 'transform 0.6s ease-out'
            }}
          >
            <div className="relative w-full h-[400px] md:h-[500px]">
              {/* Animated background glows */}
              <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-64 h-64 rounded-full bg-primary/20 blur-3xl animate-pulse-slow"></div>
              <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-48 h-48 rounded-full bg-accent/20 blur-3xl animate-pulse"></div>

              {/* 3D Element or Illustration with enhanced animations */}
              <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-80 h-80">
                <div className="relative w-full h-full">
                  {/* Central AI Icon with pulsing animation */}
                  <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-32 h-32 rounded-xl bg-dark-light border border-primary/50 flex items-center justify-center shadow-neon-blue animate-glow">
                    <span className="text-4xl font-bold gradient-text animate-pulse">AI</span>
                  </div>

                  {/* Orbiting Elements with individual animations */}
                  <div 
                    className="absolute w-16 h-16 rounded-lg bg-dark-light border border-white/10 flex items-center justify-center"
                    style={{
                      animation: 'orbit 12s linear infinite',
                      top: '0',
                      left: 'calc(50% - 8px)',
                      transformOrigin: 'center 150px'
                    }}
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8 text-primary-light animate-pulse" viewBox="0 0 20 20" fill="currentColor">
