import React, { useState, useEffect } from 'react';

const OutzonesEcosystemDiagram = () => {
  const [activeNode, setActiveNode] = useState(0);
  const [hoveredNode, setHoveredNode] = useState(null);
  const [animationPhase, setAnimationPhase] = useState(0);

  // Estrutura reorganizada baseada na imagem de referência
  const nodes = [
    {
      id: 'real-estate',
      label: 'Real Estate Digital',
      position: { x: 15, y: 25 },
      color: '#1E5BFA',
      description: 'Dados e audiência de inventários próprios geram mais resultados em nichos específicos',
      size: 'large'
    },
    {
      id: 'data',
      label: 'Dados',
      position: { x: 15, y: 50 },
      color: '#B01EFF',
      description: 'Dados próprios geram consumidores melhores com menor CAC',
      size: 'medium'
    },
    {
      id: 'client',
      label: 'Cliente',
      position: { x: 35, y: 75 },
      color: '#00D4FF',
      description: 'Consumidores qualificados atraem mais empresas (-CAC +LTV)',
      size: 'medium'
    },
    {
      id: 'martech',
      label: 'Facilitadora Martech',
      position: { x: 65, y: 75 },
      color: '#FF6B35',
      description: 'Mais empresas hospedam suas operações no ecossistema tecnológico e disponibilizam mais serviços',
      size: 'large'
    },
    {
      id: 'ai',
      label: 'A.I. Agents',
      position: { x: 50, y: 40 },
      color: '#FFE66D',
      description: 'Os dados estruturados e não estruturados capturados alimentam A.I. Agents que otimizam tarefas e promovem treinamentos no ecossistema',
      size: 'medium'
    },
    {
      id: 'community',
      label: 'Staage Get Community',
      position: { x: 85, y: 50 },
      color: '#4ECDC4',
      description: 'Capacita agências e empresas e atrai ambos para o ecossistema',
      size: 'medium'
    },
    {
      id: 'agency',
      label: 'Agências',
      position: { x: 85, y: 25 },
      color: '#FF8B94',
      description: 'Mais agências melhoram a qualidade do ensino e o interesse dos alunos',
      size: 'medium'
    }
  ];

  // Conexões reorganizadas para seguir o fluxo da imagem
  const connections = [
    { from: 0, to: 1, strength: 0.9, label: 'Gera dados' },
    { from: 1, to: 2, strength: 0.8, label: 'Qualifica' },
    { from: 2, to: 3, strength: 0.85, label: 'Atrai empresas' },
    { from: 3, to: 4, strength: 0.9, label: 'Alimenta IA' },
    { from: 4, to: 5, strength: 0.8, label: 'Capacita' },
    { from: 5, to: 6, strength: 0.75, label: 'Melhora qualidade' },
    { from: 6, to: 0, strength: 0.7, label: 'Traz empresas' },
    { from: 3, to: 5, strength: 0.6, label: 'Conecta' },
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      if (hoveredNode === null) {
        setActiveNode((prev) => (prev + 1) % nodes.length);
      }
      setAnimationPhase((prev) => (prev + 1) % 360);
    }, 4000);

    return () => clearInterval(interval);
  }, [hoveredNode]);

  const getConnectionPath = (from, to) => {
    const fromNode = nodes[from];
    const toNode = nodes[to];
    const x1 = fromNode.position.x;
    const y1 = fromNode.position.y;
    const x2 = toNode.position.x;
    const y2 = toNode.position.y;

    // Criar curvas mais suaves e organizadas
    const midX = (x1 + x2) / 2;
    const midY = (y1 + y2) / 2;
    const offset = Math.abs(x1 - x2) > Math.abs(y1 - y2) ? 8 : 12;

    return `M ${x1} ${y1} Q ${midX + offset} ${midY - offset} ${x2} ${y2}`;
  };

  const getNodeSize = (size) => {
    switch (size) {
      case 'large': return 4;
      case 'medium': return 3.2;
      default: return 2.8;
    }
  };

  const currentActive = hoveredNode !== null ? hoveredNode : activeNode;

  return (
    <div className="relative w-full h-[500px] flex items-center justify-center bg-gradient-to-br from-[#0a0a23] via-[#1a1a3a] to-[#2a1a4a] rounded-xl overflow-hidden border border-white/10">
      {/* Grid de fundo mais sutil e organizado */}
      <div className="absolute inset-0 opacity-5">
        <div className="w-full h-full bg-[radial-gradient(circle_at_center,rgba(255,255,255,0.03)_1px,transparent_1px)] bg-[length:30px_30px]"></div>
      </div>

      {/* Efeitos de fundo com cores do ecossistema */}
      <div className="absolute top-1/4 right-1/3 w-32 h-32 bg-[#1E5BFA]/5 rounded-full blur-3xl animate-pulse"></div>
      <div className="absolute bottom-1/3 left-1/4 w-24 h-24 bg-[#B01EFF]/5 rounded-full blur-3xl animate-pulse" style={{ animationDelay: '2s' }}></div>
      <div className="absolute top-1/2 right-1/4 w-20 h-20 bg-[#00D4FF]/5 rounded-full blur-3xl animate-pulse" style={{ animationDelay: '4s' }}></div>

      {/* SVG principal */}
      <svg 
        viewBox="0 0 100 100" 
        className="w-full h-full relative z-10"
        style={{ filter: 'drop-shadow(0 0 20px rgba(30, 91, 250, 0.1))' }}
      >
        <defs>
          {/* Gradientes melhorados */}
          <linearGradient id="connectionGradientFlow" x1="0%" y1="0%" x2="100%" y2="0%">
            <stop offset="0%" stopColor="#1E5BFA" stopOpacity="0.8" />
            <stop offset="30%" stopColor="#B01EFF" stopOpacity="0.6" />
            <stop offset="60%" stopColor="#00D4FF" stopOpacity="0.7" />
            <stop offset="100%" stopColor="#FFE66D" stopOpacity="0.5" />
          </linearGradient>

          <linearGradient id="connectionGradientActive" x1="0%" y1="0%" x2="100%" y2="0%">
            <stop offset="0%" stopColor="#1E5BFA" stopOpacity="1" />
            <stop offset="50%" stopColor="#B01EFF" stopOpacity="0.9" />
            <stop offset="100%" stopColor="#00D4FF" stopOpacity="0.8" />
          </linearGradient>

          <radialGradient id="centerGradient">
            <stop offset="0%" stopColor="#1E5BFA" stopOpacity="0.1" />
            <stop offset="50%" stopColor="#B01EFF" stopOpacity="0.05" />
            <stop offset="100%" stopColor="transparent" />
          </radialGradient>

          {nodes.map((node, index) => (
            <radialGradient key={`gradient-${index}`} id={`nodeGradient-${index}`}>
              <stop offset="0%" stopColor={node.color} stopOpacity="0.9" />
              <stop offset="60%" stopColor={node.color} stopOpacity="0.6" />
              <stop offset="100%" stopColor={node.color} stopOpacity="0.2" />
            </radialGradient>
          ))}

          <filter id="glow">
            <feGaussianBlur stdDeviation="3" result="coloredBlur"/>
            <feMerge> 
              <feMergeNode in="coloredBlur"/>
              <feMergeNode in="SourceGraphic"/>
            </feMerge>
          </filter>

          <filter id="softGlow">
            <feGaussianBlur stdDeviation="1.5" result="coloredBlur"/>
            <feMerge> 
              <feMergeNode in="coloredBlur"/>
              <feMergeNode in="SourceGraphic"/>
            </feMerge>
          </filter>
        </defs>

        {/* Centro do ecossistema - mais proeminente */}
        <g className="ecosystem-center">
          <circle
            cx="50"
            cy="50"
            r="18"
            fill="url(#centerGradient)"
            stroke="rgba(255,255,255,0.1)"
            strokeWidth="0.5"
            strokeDasharray="2,2"
          >
            <animateTransform
              attributeName="transform"
              attributeType="XML"
              type="rotate"
              from="0 50 50"
              to="360 50 50"
              dur="60s"
              repeatCount="indefinite"
            />
          </circle>

          <circle
            cx="50"
            cy="50"
            r="25"
            fill="none"
            stroke="rgba(255,255,255,0.05)"
            strokeWidth="0.3"
            strokeDasharray="1,3"
          >
            <animateTransform
              attributeName="transform"
              attributeType="XML"
              type="rotate"
              from="360 50 50"
              to="0 50 50"
              dur="45s"
              repeatCount="indefinite"
            />
          </circle>

          <text
            x="50"
            y="47"
            textAnchor="middle"
            fontSize="2.8"
            fill="#ffffff"
            className="font-bold select-none"
          >
            ECOSSISTEMA
          </text>
          <text
            x="50"
            y="51"
            textAnchor="middle"
            fontSize="2.2"
            fill="#B01EFF"
            className="font-light select-none"
          >
            OUTZONES
          </text>
          <text
            x="50"
            y="54.5"
            textAnchor="middle"
            fontSize="1.6"
            fill="#00D4FF"
            className="font-light select-none opacity-70"
          >
            V4.0
          </text>
        </g>

        {/* Conexões com melhor visualização */}
        <g className="connections">
          {connections.map((connection, index) => {
            const isActive = currentActive === connection.from || currentActive === connection.to;
            const isFlowActive = currentActive === connection.from;

            return (
              <g key={`connection-${index}`}>
                {/* Linha de fundo */}
                <path
                  d={getConnectionPath(connection.from, connection.to)}
                  stroke="rgba(255,255,255,0.08)"
                  strokeWidth="1.5"
                  fill="none"
                  className="transition-all duration-1000 ease-in-out"
                />

                {/* Linha principal com gradiente */}
                <path
                  d={getConnectionPath(connection.from, connection.to)}
                  stroke={isActive ? "url(#connectionGradientActive)" : "url(#connectionGradientFlow)"}
                  strokeWidth={isActive ? "1" : "0.7"}
                  fill="none"
                  opacity={isActive ? 1 : 0.4}
                  className="transition-all duration-1000 ease-in-out"
                  strokeDasharray={isActive ? "0" : "2,2"}
                />

                {/* Partícula de fluxo animada */}
                {isFlowActive && (
                  <circle
                    r="1"
                    fill="#ffffff"
                    opacity="0.9"
                    filter="url(#softGlow)"
                  >
                    <animateMotion
                      dur="4s"
                      repeatCount="indefinite"
                      path={getConnectionPath(connection.from, connection.to)}
                    />
                    <animate
                      attributeName="opacity"
                      values="0;1;0.8;1;0"
                      dur="4s"
                      repeatCount="indefinite"
                    />
                  </circle>
                )}
              </g>
            );
          })}
        </g>

        {/* Nós com tamanhos diferenciados */}
        <g className="nodes">
          {nodes.map((node, index) => {
            const isActive = currentActive === index;
            const isHovered = hoveredNode === index;
            const nodeSize = getNodeSize(node.size);

            return (
              <g key={`node-${index}`}>
                {/* Pulso para nó ativo */}
                {isActive && (
                  <circle
                    cx={node.position.x}
                    cy={node.position.y}
                    r={nodeSize + 2}
                    fill="none"
                    stroke={node.color}
                    strokeWidth="0.3"
                    opacity="0.6"
                  >
                    <animate
                      attributeName="r"
                      values={`${nodeSize};${nodeSize + 6};${nodeSize}`}
                      dur="3s"
                      repeatCount="indefinite"
                    />
                    <animate
                      attributeName="opacity"
                      values="0.6;0.1;0.6"
                      dur="3s"
                      repeatCount="indefinite"
                    />
                  </circle>
                )}

                {/* Área de hover expandida */}
                <circle
                  cx={node.position.x}
                  cy={node.position.y}
                  r="8"
                  fill="transparent"
                  className="cursor-pointer"
                  onMouseEnter={() => setHoveredNode(index)}
                  onMouseLeave={() => setHoveredNode(null)}
                />

                {/* Nó principal com tamanho baseado na importância */}
                <circle
                  cx={node.position.x}
                  cy={node.position.y}
                  r={isActive ? nodeSize + 0.5 : nodeSize}
                  fill={`url(#nodeGradient-${index})`}
                  stroke={node.color}
                  strokeWidth={isActive ? "0.8" : "0.5"}
                  className="transition-all duration-500 ease-out pointer-events-none"
                  filter={isActive ? "url(#glow)" : "url(#softGlow)"}
                />

                {/* Labels melhorados - aumentei a distância do nó */}
                <text
                  x={node.position.x}
                  y={node.position.y - nodeSize - 4}
                  textAnchor="middle"
                  fontSize="2.2"
                  fill="#ffffff"
                  className="font-semibold pointer-events-none select-none"
                  opacity={isActive || isHovered ? 1 : 0.7}
                >
                  {node.label.split(' ')[0]}
                </text>
                {node.label.split(' ').length > 1 && (
                  <>
                    <text
                      x={node.position.x}
                      y={node.position.y - nodeSize - 1}
                      textAnchor="middle"
                      fontSize="1.8"
                      fill="#ffffff"
                      className="font-light pointer-events-none select-none"
                      opacity={isActive || isHovered ? 0.8 : 0.5}
                    >
                      {node.label.split(' ')[1]}
                    </text>
                    {node.label.split(' ').length > 2 && (
                      <text
                        x={node.position.x}
                        y={node.position.y - nodeSize + 1.5}
                        textAnchor="middle"
                        fontSize="1.6"
                        fill="#ffffff"
                        className="font-light pointer-events-none select-none"
                        opacity={isActive || isHovered ? 0.7 : 0.4}
                      >
                        {node.label.split(' ').slice(2).join(' ')}
                      </text>
                    )}
                  </>
                )}
              </g>
            );
          })}
        </g>
      </svg>

      {/* Painel de informação melhorado - movido mais para baixo */}
      <div className="absolute bottom-6 left-6 right-6 bg-black/80 backdrop-blur-xl rounded-xl p-6 border border-white/20 z-20 shadow-2xl">
        <div className="flex items-start gap-4">
          <div className="relative flex-shrink-0">
            <div 
              className="w-4 h-4 rounded-full"
              style={{ backgroundColor: nodes[currentActive].color }}
            >
              <div 
                className="w-full h-full rounded-full animate-ping"
                style={{ backgroundColor: nodes[currentActive].color, animationDuration: '2s' }}
              ></div>
            </div>
          </div>
          <div className="min-w-0 flex-1">
            <h4 className="text-base font-semibold text-white mb-2">
              {nodes[currentActive].label}
            </h4>
            <p className="text-sm text-white/80 leading-relaxed">
              {nodes[currentActive].description}
            </p>
          </div>
        </div>
      </div>

      {/* Indicador de eficiência melhorado */}
      <div className="absolute top-4 right-4 z-20">
        <div className="bg-black/70 backdrop-blur-xl rounded-xl p-4 border border-white/20">
          <div className="text-center">
            <div className="text-3xl font-bold bg-gradient-to-r from-[#1E5BFA] to-[#B01EFF] bg-clip-text text-transparent">
              {85 + Math.floor(Math.sin(animationPhase * 0.03) * 5)}%
            </div>
            <div className="text-xs text-white/70 uppercase tracking-wider mt-1">
              Eficiência Global
            </div>
            <div className="text-xs text-white/50 mt-1">
              Efeito Rede V4
            </div>
          </div>
        </div>
      </div>

      {/* Título do diagrama */}
      <div className="absolute top-4 left-4 z-20">
        <div className="bg-black/70 backdrop-blur-xl rounded-xl p-4 border border-white/20">
          <h3 className="text-lg font-bold text-white mb-1">
            O Ganha-Ganha do
          </h3>
          <h3 className="text-lg font-bold bg-gradient-to-r from-[#1E5BFA] to-[#B01EFF] bg-clip-text text-transparent">
            Efeito-Rede V4
          </h3>
        </div>
      </div>
    </div>
  );
};

const DifferentialsSection = () => {
  return (
    <section id="differentials" className="relative min-h-screen flex items-center overflow-hidden bg-[#0a0a23]">
      <div className="absolute inset-0 opacity-5">
        <div className="w-full h-full bg-[radial-gradient(circle_at_center,rgba(255,255,255,0.1)_1px,transparent_1px)] bg-[length:40px_40px]"></div>
      </div>
      <div className="container mx-auto px-6 relative z-10">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
          <div className="space-y-8">
            <h2 className="text-3xl md:text-4xl font-light mb-4">
              Por que Escolher a <span className="bg-gradient-to-r from-[#1E5BFA] to-[#B01EFF] bg-clip-text text-transparent font-normal">Outzones</span>
            </h2>
            <p className="text-white/70 max-w-xl text-lg">
              Nossa abordagem "AI-First" e foco em pequenas e médias empresas nos permite oferecer 
              soluções personalizadas que realmente impulsionam resultados através de um ecossistema inteligente.
            </p>

            <div className="space-y-6">
              {/* Diferencial 1 */}
              <div className="flex items-start group hover:bg-white/5 p-4 rounded-lg transition-all">
                <div className="w-12 h-12 rounded-lg bg-gradient-to-br from-[#1E5BFA]/20 to-[#1E5BFA]/10 flex items-center justify-center mr-4 flex-shrink-0 group-hover:scale-110 transition-transform">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-[#1E5BFA]" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" />
                  </svg>
                </div>
                <div>
                  <h3 className="text-xl font-medium mb-2 text-white">Soluções Personalizadas</h3>
                  <p className="text-white/70">
                    Desenvolvemos soluções sob medida para as necessidades específicas do seu negócio, 
                    garantindo máxima eficiência e resultados mensuráveis.
                  </p>
                </div>
              </div>

              {/* Diferencial 2 - Destaque especial */}
              <div className="flex items-start group hover:bg-gradient-to-r hover:from-[#B01EFF]/10 hover:to-transparent p-4 rounded-lg transition-all border border-[#B01EFF]/20">
                <div className="w-12 h-12 rounded-lg bg-gradient-to-br from-[#B01EFF]/20 to-[#B01EFF]/10 flex items-center justify-center mr-4 flex-shrink-0 group-hover:scale-110 transition-transform">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-[#B01EFF]" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 21a9.004 9.004 0 0 0 8.716-6.747M12 21a9.004 9.004 0 0 1-8.716-6.747M12 21c2.485 0 4.5-4.03 4.5-9S14.485 3 12 3m0 18c-2.485 0-4.5-4.03-4.5-9S9.515 3 12 3m0 0a8.997 8.997 0 0 1 7.843 4.582M12 3a8.997 8.997 0 0 0-7.843 4.582m15.686 0A11.953 11.953 0 0 1 12 10.5c-2.998 0-5.74-1.1-7.843-2.918m15.686 0A8.959 8.959 0 0 1 21 12c0 .778-.099 1.533-.284 2.253m0 0A17.919 17.919 0 0 1 12 16.5c-3.162 0-6.133-.815-8.716-2.247m0 0A9.015 9.015 0 0 1 3 12c0-1.605.42-3.113 1.157-4.418" />
                  </svg>
                </div>
                <div>
                  <h3 className="text-xl font-medium mb-2 text-white">Ecossistema Inteligente Outzones V4</h3>
                  <p className="text-white/70 mb-2">
                    A Outzones não entrega apenas ferramentas — construímos um ecossistema onde cada interação gera valor. 
                    Nossos serviços conectam empresas, agências, clientes e inteligência artificial para criar um efeito de rede sustentável.
                  </p>
                  <div className="text-sm text-[#B01EFF] font-medium">
                    → Visualize o ganha-ganha do efeito-rede no diagrama ao lado
                  </div>
                </div>
              </div>

              {/* Diferencial 3 */}
              <div className="flex items-start group hover:bg-white/5 p-4 rounded-lg transition-all">
                <div className="w-12 h-12 rounded-lg bg-gradient-to-br from-[#00D4FF]/20 to-[#00D4FF]/10 flex items-center justify-center mr-4 flex-shrink-0 group-hover:scale-110 transition-transform">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-[#00D4FF]" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
                  </svg>
                </div>
                <div>
                  <h3 className="text-xl font-medium mb-2 text-white">Implementação Rápida</h3>
                  <p className="text-white/70">
                    Nossa metodologia ágil permite implementações rápidas e eficientes, 
                    com resultados visíveis em semanas, não meses.
                  </p>
                </div>
              </div>

              {/* Diferencial 4 */}
              <div className="flex items-start group hover:bg-white/5 p-4 rounded-lg transition-all">
                <div className="w-12 h-12 rounded-lg bg-gradient-to-br from-[#FFE66D]/20 to-[#FFE66D]/10 flex items-center justify-center mr-4 flex-shrink-0 group-hover:scale-110 transition-transform">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-[#FFE66D]" fill="none" viewBox="0 0 24 24" stroke="currentColor">
