import React, { useEffect, useState, useMemo, useCallback } from 'react';

// Constantes para valores de configuração
const PARALLAX_INTENSITY = {
  particles: { blue: -0.3, purple: -0.5 },
  content: -2,
  illustration: -5
};

const ANIMATION_CONFIG = {
  particleCount: { desktop: 25, mobile: 8 },
  particleSizes: { min: 2, max: 6 },
  animationDuration: { min: 15, max: 25 },
  glowRadius: { min: 2, max: 7 }
};

// Hook para detectar preferência de movimento reduzido
const useReducedMotion = () => {
  const [prefersReducedMotion, setPrefersReducedMotion] = useState(false);

  useEffect(() => {
    const mediaQuery = window.matchMedia('(prefers-reduced-motion: reduce)');
    setPrefersReducedMotion(mediaQuery.matches);

    const handleChange = (e) => setPrefersReducedMotion(e.matches);
    mediaQuery.addEventListener('change', handleChange);

    return () => mediaQuery.removeEventListener('change', handleChange);
  }, []);

  return prefersReducedMotion;
};

// Hook para detectar dispositivo móvel
const useIsMobile = () => {
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const checkMobile = () => setIsMobile(window.innerWidth < 768);
    checkMobile();

    window.addEventListener('resize', checkMobile);
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  return isMobile;
};

// Componente de partícula otimizado
const AnimatedParticle = React.memo(({ particle, mousePosition, intensity, reducedMotion }) => {
  const style = useMemo(() => {
    const baseStyle = {
      width: `${particle.size}px`,
      height: `${particle.size}px`,
      top: `${particle.y}%`,
      left: `${particle.x}%`,
      opacity: particle.opacity,
      background: particle.background,
      boxShadow: particle.boxShadow,
    };

    if (reducedMotion) {
      return baseStyle;
    }

    return {
      ...baseStyle,
      animation: `floatParticle ${particle.duration}s linear infinite`,
      animationDelay: `${particle.delay}s`,
      transform: `translate(${mousePosition.x * intensity}px, ${mousePosition.y * intensity}px)`,
      transition: 'transform 0.8s ease-out'
    };
  }, [particle, mousePosition, intensity, reducedMotion]);

  return (
    <div 
      className="absolute rounded-full"
      style={style}
      aria-hidden="true"
    />
  );
});

// Componente principal otimizado
const HeroSection = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const isMobile = useIsMobile();
  const prefersReducedMotion = useReducedMotion();

  // Otimização do mouse tracking com throttling
  const handleMouseMove = useCallback((e) => {
    if (isMobile || prefersReducedMotion) return;

    setMousePosition({
      x: e.clientX / window.innerWidth - 0.5,
      y: e.clientY / window.innerHeight - 0.5
    });
  }, [isMobile, prefersReducedMotion]);

  useEffect(() => {
    if (isMobile || prefersReducedMotion) return;

    let timeoutId;
    const throttledHandler = (e) => {
      clearTimeout(timeoutId);
      timeoutId = setTimeout(() => handleMouseMove(e), 16); // ~60fps
    };

    window.addEventListener('mousemove', throttledHandler);
    return () => {
      window.removeEventListener('mousemove', throttledHandler);
      clearTimeout(timeoutId);
    };
  }, [handleMouseMove, isMobile, prefersReducedMotion]);

  // Geração otimizada de partículas
  const particles = useMemo(() => {
    const count = isMobile ? ANIMATION_CONFIG.particleCount.mobile : ANIMATION_CONFIG.particleCount.desktop;
    const blueCount = Math.floor(count * 0.6);
    const purpleCount = count - blueCount;

    const generateParticles = (count, colorType) => {
      return Array.from({ length: count }, (_, i) => {
        const size = Math.random() * (ANIMATION_CONFIG.particleSizes.max - ANIMATION_CONFIG.particleSizes.min) + ANIMATION_CONFIG.particleSizes.min;
        const opacity = Math.random() * 0.5 + 0.2;
        const duration = Math.random() * 10 + ANIMATION_CONFIG.animationDuration.min;
        const delay = Math.random() * 5;
        const glowRadius = Math.random() * (ANIMATION_CONFIG.glowRadius.max - ANIMATION_CONFIG.glowRadius.min) + ANIMATION_CONFIG.glowRadius.min;

        const colors = {
          blue: {
            bg: `rgba(${30 + Math.random() * 50}, ${91 + Math.random() * 50}, ${250 + Math.random() * 5}, ${Math.random() * 0.3 + 0.1})`,
            shadow: `0 0 ${glowRadius}px rgba(30, 91, 250, ${Math.random() * 0.3 + 0.1})`
          },
          purple: {
            bg: `rgba(${176 + Math.random() * 30}, ${30 + Math.random() * 30}, 255, ${Math.random() * 0.3 + 0.1})`,
            shadow: `0 0 ${glowRadius}px rgba(176, 30, 255, ${Math.random() * 0.3 + 0.1})`
          }
        };

        return {
          id: `${colorType}-${i}`,
          size,
          x: Math.random() * 100,
          y: Math.random() * 100,
          opacity,
          duration,
          delay,
          background: colors[colorType].bg,
          boxShadow: colors[colorType].shadow,
          type: colorType
        };
      });
    };

    return [
      ...generateParticles(blueCount, 'blue'),
      ...generateParticles(purpleCount, 'purple')
    ];
  }, [isMobile]);

  // Estilos para elementos principais com parallax
  const contentStyle = useMemo(() => {
    if (isMobile || prefersReducedMotion) return {};

    return {
      transform: `translate(${mousePosition.x * PARALLAX_INTENSITY.content}px, ${mousePosition.y * (PARALLAX_INTENSITY.content * 4)}px)`,
      transition: 'transform 0.6s ease-out'
    };
  }, [mousePosition, isMobile, prefersReducedMotion]);

  const illustrationStyle = useMemo(() => {
    if (isMobile || prefersReducedMotion) return {};

    return {
      transform: `translate(${mousePosition.x * PARALLAX_INTENSITY.illustration}px, ${mousePosition.y * (PARALLAX_INTENSITY.illustration * 4)}px)`,
      transition: 'transform 0.6s ease-out'
    };
  }, [mousePosition, isMobile, prefersReducedMotion]);

  return (
    <section 
      id="home" 
      className="section bg-hero min-h-screen flex items-center relative overflow-hidden"
      role="banner"
    >
      <div className="grid-lines absolute inset-0 opacity-20" aria-hidden="true"></div>

      {/* Partículas animadas otimizadas */}
      <div 
        className="absolute inset-0 z-0" 
        aria-hidden="true"
        role="presentation"
      >
        {particles.map((particle) => (
          <AnimatedParticle
            key={particle.id}
            particle={particle}
            mousePosition={mousePosition}
            intensity={PARALLAX_INTENSITY.particles[particle.type]}
            reducedMotion={prefersReducedMotion}
          />
        ))}
      </div>

      <div className="container-custom relative z-10">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 lg:gap-12 items-center">
          {/* Conteúdo principal */}
          <div className="space-y-6 lg:space-y-8" style={contentStyle}>
            <h1 className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl font-light leading-tight">
              Transforme seu Negócio com{' '}
              <span className="gradient-text font-normal">IA Personalizada</span>
            </h1>

            <p className="text-base lg:text-lg text-white/80 max-w-xl leading-relaxed">
              Soluções de inteligência artificial seguras e acessíveis para pequenas e médias empresas. 
              Automatize processos, impulsione resultados e destaque-se no mercado.
            </p>

            <div className="flex flex-col sm:flex-row gap-4">
              <a 
                href="#solutions" 
                className={`btn-primary ${!prefersReducedMotion ? 'animate-pulse-slow' : ''}`}
                aria-label="Conhecer nossas soluções de IA"
              >
                Conheça Nossas Soluções
              </a>
              <a 
                href="#contact" 
                className={`btn-secondary ${!prefersReducedMotion ? 'hover:scale-105 transition-transform' : ''}`}
                aria-label="Entrar em contato para consultoria"
              >
                Como podemos ajudar?
              </a>
            </div>
          </div>

          {/* Ilustração 3D */}
          <div className="relative mt-8 lg:mt-0" style={illustrationStyle}>
            <div className="relative w-full h-[350px] md:h-[400px] lg:h-[500px]">
              {/* Glows de fundo */}
              {!prefersReducedMotion && (
                <>
                  <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-48 lg:w-64 h-48 lg:h-64 rounded-full bg-primary/20 blur-3xl animate-pulse-slow" aria-hidden="true"></div>
                  <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-36 lg:w-48 h-36 lg:h-48 rounded-full bg-accent/20 blur-3xl animate-pulse" aria-hidden="true"></div>
                </>
              )}

              {/* Elemento 3D principal */}
              <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-64 lg:w-80 h-64 lg:h-80">
                <div className="relative w-full h-full">
                  {/* Ícone central de IA */}
                  <div className={`absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-24 lg:w-32 h-24 lg:h-32 rounded-xl bg-dark-light border border-primary/50 flex items-center justify-center shadow-neon-blue ${!prefersReducedMotion ? 'animate-glow' : ''}`}>
                    <span className={`text-2xl lg:text-4xl font-bold gradient-text ${!prefersReducedMotion ? 'animate-pulse' : ''}`}>
                      AI
                    </span>
                  </div>

                  {/* Elementos orbitais */}
                  {[
                    { icon: 'bookmark', delay: '0s', position: 'top', color: 'primary-light' },
                    { icon: 'code', delay: '-3s', position: 'bottom', color: 'accent' },
                    { icon: 'chart', delay: '-6s', position: 'right', color: 'primary' },
                    { icon: 'lightning', delay: '-9s', position: 'left', color: 'accent-light' }
                  ].map((item, index) => (
                    <div
                      key={index}
                      className={`absolute w-12 lg:w-16 h-12 lg:h-16 rounded-lg bg-dark-light border border-white/10 flex items-center justify-center ${
                        !prefersReducedMotion ? 'animate-orbit' : ''
                      }`}
                      style={{
                        ...(item.position === 'top' && { top: '0', left: 'calc(50% - 24px)' }),
                        ...(item.position === 'bottom' && { bottom: '0', left: 'calc(50% - 24px)' }),
                        ...(item.position === 'right' && { top: 'calc(50% - 24px)', right: '0' }),
                        ...(item.position === 'left' && { top: 'calc(50% - 24px)', left: '0' }),
                        ...((!prefersReducedMotion && item.position === 'top') && {
                          animation: 'orbit 12s linear infinite',
                          transformOrigin: 'center 150px'
                        }),
                        ...((!prefersReducedMotion && item.position === 'bottom') && {
                          animation: 'orbit 12s linear infinite',
                          animationDelay: item.delay,
                          transformOrigin: 'center -150px'
                        }),
                        ...((!prefersReducedMotion && item.position === 'right') && {
                          animation: 'orbit 12s linear infinite',
                          animationDelay: item.delay,
                          transformOrigin: '-150px center'
                        }),
                        ...((!prefersReducedMotion && item.position === 'left') && {
                          animation: 'orbit 12s linear infinite',
                          animationDelay: item.delay,
                          transformOrigin: '150px center'
                        })
                      }}
                      aria-hidden="true"
                    >
                      <div className={`w-6 lg:w-8 h-6 lg:h-8 text-${item.color} ${!prefersReducedMotion ? 'animate-pulse' : ''}`}>
                        {/* Ícones simplificados */}
                        {item.icon === 'bookmark' && (
                          <svg viewBox="0 0 20 20" fill="currentColor">
                            <path fillRule="evenodd" d="M3 5a2 2 0 012-2h10a2 2 0 012 2v10a2 2 0 01-2 2H5a2 2 0 01-2-2V5zm11 1H6v8l4-2 4 2V6z" clipRule="evenodd" />
                          </svg>
                        )}
                        {item.icon === 'code' && (
                          <svg viewBox="0 0 20 20" fill="currentColor">
                            <path fillRule="evenodd" d="M12.316 3.051a1 1 0 01.633 1.265l-4 12a1 1 0 11-1.898-.632l4-12a1 1 0 011.265-.633zM5.707 6.293a1 1 0 010 1.414L3.414 10l2.293 2.293a1 1 0 11-1.414 1.414l-3-3a1 1 0 010-1.414l3-3a1 1 0 011.414 0zm8.586 0a1 1 0 011.414 0l3 3a1 1 0 010 1.414l-3 3a1 1 0 11-1.414-1.414L16.586 10l-2.293-2.293a1 1 0 010-1.414z" clipRule="evenodd" />
                          </svg>
                        )}
                        {item.icon === 'chart' && (
                          <svg viewBox="0 0 20 20" fill="currentColor">
                            <path d="M2 10a8 8 0 018-8v8h8a8 8 0 11-16 0z" />
                            <path d="M12 2.252A8.014 8.014 0 0117.748 8H12V2.252z" />
                          </svg>
                        )}
                        {item.icon === 'lightning' && (
                          <svg viewBox="0 0 20 20" fill="currentColor">
                            <path fillRule="evenodd" d="M11.3 1.046A1 1 0 0112 2v5h4a1 1 0 01.82 1.573l-7 10A1 1 0 018 18v-5H4a1 1 0 01-.82-1.573l7-10a1 1 0 011.12-.38z" clipRule="evenodd" />
                          </svg>
                        )}
                      </div>
                    </div>
                  ))}

                  {/* Linhas de conexão */}
                  {!prefersReducedMotion && (
                    <div className="absolute inset-0 z-[-1]" aria-hidden="true">
                      <svg className="w-full h-full" viewBox="0 0 200 200">
                        <line x1="100" y1="100" x2="100" y2="20" stroke="rgba(30, 91, 250, 0.3)" strokeWidth="1" className="animate-pulse-slow" />
                        <line x1="100" y1="100" x2="100" y2="180" stroke="rgba(176, 30, 255, 0.3)" strokeWidth="1" className="animate-pulse-slow" style={{animationDelay: '0.5s'}} />
                        <line x1="100" y1="100" x2="180" y2="100" stroke="rgba(30, 91, 250, 0.3)" strokeWidth="1" className="animate-pulse-slow" style={{animationDelay: '1s'}} />
                        <line x1="100" y1="100" x2="20" y2="100" stroke="rgba(176, 30, 255, 0.3)" strokeWidth="1" className="animate-pulse-slow" style={{animationDelay: '1.5s'}} />
                      </svg>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Elementos decorativos otimizados */}
      {!prefersReducedMotion && (
        <>
          <div className="absolute bottom-10 left-10 w-24 lg:w-32 h-24 lg:h-32 bg-primary/5 rounded-full blur-3xl animate-pulse-slow" aria-hidden="true"></div>
          <div className="absolute top-20 right-20 w-32 lg:w-40 h-32 lg:h-40 bg-accent/5 rounded-full blur-3xl animate-pulse" style={{animationDelay: '1s'}} aria-hidden="true"></div>
          <div className="absolute bottom-1/4 right-1/4 w-16 lg:w-24 h-16 lg:h-24 bg-primary/10 rounded-full blur-2xl animate-float" style={{animationDuration: '7s'}} aria-hidden="true"></div>
          <div className="absolute top-1/3 left-1/3 w-12 lg:w-16 h-12 lg:h-16 bg-accent/10 rounded-full blur-xl animate-float" style={{animationDuration: '9s', animationDelay: '1s'}} aria-hidden="true"></div>
        </>
      )}
    </section>
  );
};

export default HeroSection;
